class Solution {
    public int minimumDifference(int[] nums, int k) {
        int n = nums.length;
        int cnt1[] = new int[31];
        int cnt0[] = new int[31];
        Arrays.fill(cnt1,1);
        int l = 0; int r = 0;
        int and = (1<<30)-1; int ans = Integer.MAX_VALUE;
        while(r<n)
        {
            and =  and&nums[r];
            ans = Math.min(ans,Math.abs(k-and));
             // System.out.println(and);
            add(nums[r],cnt1,cnt0);
            if(and==k) return 0;
            while(l<=r&&and<k)
            {
               and =  remove(nums[l],cnt1,cnt0,and);
               ans = Math.min(ans,Math.abs(k-and));
               l++;
            }
            r++;
        }
        return ans;
    }
    public int remove(int val,int cnt1[], int cnt0[],int and)
    {
        int bit = 0;
        while(bit<=30)
        {
            int  t = 0;
            if((val&(1<<bit))>0) t=1;
            if(t==0) cnt0[bit]--;
            if(t==1) cnt1[bit]--;
            if(cnt0[bit]==0&&cnt1[bit]>0)
            {
                and = and | (1<<bit) ;
            }
            bit++;
        }
        return and;
    }
    public void add(int val,int cnt1[], int cnt0[])
    {
        int bit = 0;
        while(bit<=30)
        {
            int t =0;
            if((val&(1<<bit))>0) t=1;
            if(t==0) cnt0[bit]++;
            if(t==1) cnt1[bit]++;
            bit++;
        }
    }
}
