class Solution {
public:
    int calc(int l,int r,vector<vector<int>> &pref)
    {
        int val=0;
        for(int i=0;i<32;i++)
        {
            int len=pref[r][i]-pref[l-1][i];
            if(len==(r-l+1))
            {
                val+=(1<<i);
            }
        }
        return val;
    }
    int minimumDifference(vector<int>& a, int k) {
        int n=a.size();
        vector<vector<int>> pref(n+1,vector<int> (32,0));
        for(int i=1;i<=n;i++)
        {
            for(int j=0;j<32;j++)
            {
                pref[i][j]+=pref[i-1][j];
                if((a[i-1]>>j)&1)
                {
                    pref[i][j]++;
                }
            }
        }
        int ans=1e9;
        for(int i=1;i<=n;i++)
        {
            int ele=a[i-1];
            int low=i,high=n;
            while(low<=high)
            {
                int mid=(high-low)/2+low;
                if(calc(i,mid,pref)>=k){low=mid+1;}
                else{high=mid-1;}
            }
            if(high<i)
            {
                ans=min(ans,abs(k-ele));
            }
            else
            {
                int prev=calc(i,high,pref);
                int next=calc(i,min(n,high+1),pref);
                ans=min({ans,abs(k-prev),abs(k-next)});
            }
        }
        return ans;
    }
};
