class Solution {
public:
    vector<int>seg;
    int query(int i,int rl,int rr,int ql,int qr)
    {
        if(qr<rl||rr<ql)return INT_MAX;
        if((ql<=rl)&&(rr<=qr))return seg[i];
        int m=(rr+rl)/2;
        int l=query(2*i,rl,m,ql,qr);
        int r=query(2*i+1,m+1,rr,ql,qr);
        // cout<<"@ "<<rl<<" "<<rr<<endl;
        // cout<<"$ "<<l<<" "<<r<<endl;
        return (l&r);
    }

    int minimumDifference(vector<int>& a, int k) {
        int sz=a.size();
        while(sz&(sz-1))sz++;
        seg.clear();
        seg.resize(2*sz);
        for(int i=0;i<a.size();i++)
        {
            seg[i+sz]=a[i];
            // cout<<(i+sz)<<" "<<seg[i+sz]<<endl;
        }
        for(int i=sz-1;i>=1;i--)
        {
            seg[i]=seg[2*i]&seg[2*i+1];
            // cout<<i<<" "<<seg[i]<<endl;;
        }

        int ans=INT_MAX,n=a.size();
        // cout<<query(1,0,sz-1,1,2)<<" ";
        for(int i=0;i<a.size();i++)
        {
            if(a[i]<=k)
            {
                ans=min(ans,k-a[i]);
                // cout<<"1";
                continue;
            }
            int q=0;
            q=query(1,0,sz-1,i,n-1);
            // cout<<"@ "<<q<<endl;
            if(q>k)
            {
                ans=min(ans,q-k);
                // cout<<"2";
                continue;
            }
            int l=i,r=a.size()-1;
            while(r-l>1)
            {
                int m=(r+l)/2;
                int q1=query(1,0,sz-1,i,m);
                // cout<<"! "<<q1<<endl;
                if(q1<=k)
                {
                    r=m;
                }
                else l=m;
            }
            int q2=query(1,0,sz-1,i,r);
            ans=min(ans,abs(q2-k));
            q2=query(1,0,sz-1,i,l);
            ans=min(ans,abs(q2-k));

        }
        return ans;
    }
};
