class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
       int n = nums.size();

        vector <vector <int> > st(n, vector <int> (20, 0));
        vector <int> log(n + 1);

        log[1] = 0;

        for(int i = 2; i<=n; i++) log[i] = 1 + log[i/2];


        for(int i = 0; i<n; i++) st[i][0] = nums[i];

        for(int j = 1; j<20; j++){
            for(int i = 0; i<n; i++){
                if(i + (1 << j) <= n) st[i][j] = (st[i][j - 1] & st[i + (1 << (j - 1))][j - 1]);
            }
        }

        auto Min = [&](int l, int r){
            int len = r - l + 1;
            if(1 << log[len] == len) return st[l][ log[len] ];

            return (st[l][ log[len] ]&st[r - (1 << log[len]) + 1][ log[len] ]);
        };

        int mini = INT_MAX;

        int len1 = nums.size() - 1;

        for(int i=0;i<nums.size();i++){
             int s = i , e = nums.size() - 1;
             int ind = -1;

              while(s<=e){
                  int mid = (s+e)>>1;

                  int temp = 0;

                  if(mid!=i)
                   temp = Min(i,mid);
                  else temp = nums[i];

                  if(i==0) cout << temp << endl;

                  if(temp >=k){

                      ind = mid;;
                      s = mid + 1;
                  }
                  else e = mid - 1;
              }



            if(ind!=-1) {
                mini = min(mini , abs(k - Min(i,ind)));
                mini = min(mini , abs(k - Min(i,max(i,ind-1))));
            }



            s = i , e = nums.size() - 1;
             ind = -1;

            while(s<=e){
                  int mid = (s+e)>>1;

                 int temp = 0;
                  if(mid!=i)
                   temp = Min(i,mid);
                  else temp = nums[i];

                  if(temp <= k){
                      ind = mid;
                      e = mid - 1;
                  }
                  else s = mid + 1;
              }

             if(ind!=-1) {
                mini = min(mini , abs(k - Min(i,ind)));
                mini = min(mini , abs(k - Min(i,min(len1,ind+1))));
            }






        }

        // for(int i=0;i<nums.size();i++){
        //     for(int j=i;j<nums.size();j++){
        //         cout << Min(i,j) << endl;
        //     }
        // }

        return mini;


    }
};

