class Solution {
public:
    int minimumDifference(vector<int>& a, int k) {
        const int bits=30;
        vector<vector<int>> pre(a.size()+1,vector<int> (bits));
        for(int i=0;i<(int)a.size();i++){
            for(int j=0;j<bits;j++){
                pre[i+1][j]=pre[i][j];
                if((a[i]>>j)&1) {
                    pre[i+1][j]++;
                }
            }
        }
        function<int(int,int)>lr_and=[&](int l,int r){
            int len=r-l+1;
            int res=0;
            for(int i=0;i<bits;i++)  {
                if(pre[r+1][i]-pre[l][i] == len) {
                    res|=(1<<i);
                }
            }
            return res;
        };

        function<array<int,2>(int)>binary_search=[&](int i)->array<int,2>{
            int l=i,h=a.size()-1,ind1=i,ind2=i;
            while(l<=h){
                int mid=(l+h)>>1;
                int AND=lr_and(i,mid);
                if(AND>=k) {
                    ind1=mid;
                    l=mid+1;
                }
                else {
                    ind2=mid;
                    h=mid-1;
                }
            }
            return {ind1,ind2};
        };
        int res=1e9;
        for(int i=0;i<(int)(a.size());i++){
            array<int,2> b=binary_search(i);
            int v1=lr_and(i,b[0]);
            int v2=lr_and(i,b[1]);
            res=min(res,abs(v1-k));
            res=min(res,abs(v2-k));
        }
        return res;
    }
};
