class SegmentTree {
public:
    vector<int> segTree;
    int size;

    SegmentTree(vector<int>& inputArray) {
        size = inputArray.size();
        segTree.resize(4 * size);
        buildTree(inputArray, 0, 0, size - 1);
    }

    void buildTree(vector<int>& inputArray, int node, int start, int end) {
        if (start == end) {
            segTree[node] = inputArray[start];
        } else {
            int mid = (start + end) / 2;
            buildTree(inputArray, 2 * node + 1, start, mid);
            buildTree(inputArray, 2 * node + 2, mid + 1, end);
            segTree[node] = segTree[2 * node + 1] & segTree[2 * node + 2];
        }
    }

    int queryRange(int left, int right) {
        return query(0, 0, size - 1, left, right);
    }

    int query(int node, int start, int end, int left, int right) {
        if (right < start || end < left) {
            return INT_MAX;
        }
        if (left <= start && end <= right) {
            return segTree[node];
        }
        int mid = (start + end) / 2;
        int leftQuery = query(2 * node + 1, start, mid, left, right);
        int rightQuery = query(2 * node + 2, mid + 1, end, left, right);
        return leftQuery & rightQuery;
    }
};

class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int length = nums.size();
        SegmentTree segmentTree(nums);
        int minDifference = INT_MAX;

        for (int i = 0; i < length; i++) {
            int left = i - 1, right = length - 1, mid;

            while (right - left > 1) 
            {
                mid = left + (right - left) / 2;
                if (segmentTree.queryRange(i, mid) >= k)
                    left = mid;
                else
                    right = mid;
            }

            if (left != i - 1)
                minDifference = min(minDifference, abs(segmentTree.queryRange(i, left) - k));
            minDifference = min(minDifference, abs(k - segmentTree.queryRange(i, right)));
        }
        return minDifference;
    }
};
