class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        zeros = [0] * 32

        def a(x):
            for i in range(32):
                if ((x >> i) & 1) == 0:zeros[i] += 1
        def b(x):
            for i in range(32):
                if ((x >> i) & 1) == 0:zeros[i] -= 1
        def c():
            res = 0
            for i in range(32):
                if zeros[i] == 0:res ^= (1 << i)
            return res

        left,n,ans = 0,len(nums),float('inf')

        for right in range(n):
            a(nums[right])
            x = c()
            ans = min(ans, abs(x - k))
            while left < right and x < k:
                b(nums[left])
                left += 1
                x = c()
                ans = min(ans, abs(x - k))

        return ans
