class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> v(32,0),p(32,1);
        int temp=1;
        for(int i=1;i<31;i++){
            temp*=2;
            p[i]=temp;
        }
        int j=-1,i=0,x=0,y=0,value=0,ans=INT_MAX;
        while(i<n){
            ans=min(ans,abs(nums[i]-k));
            // cout<<i<<" "<<ans<<endl;
            if(nums[i]>=k){
                j=i;
                value=nums[i];
                x=nums[i];
                y=0;
                while(x>0){
                   if(x%2){
                       v[y]++;
                   }
                    x/=2;
                    y++;
                }
                i++;
                break;
            }
            else i++;
        }
        while(i<n){
            if((value&nums[i])<k && (j<i)){
                x=nums[j];
                y=0;
                while(x>0){
                   if(x%2){
                       v[y]--;
                   }
                    x/=2;
                    y++;
                }
                ans=min(ans,abs((value&nums[i])-k));
                // cout<<i<<" "<<ans<<" "<<j<<" "<<value<<endl;
                j++;
                value=calculate(v,p,i-j);
            }
            else {
                x=nums[i];
                y=0;
                while(x>0){
                   if(x%2){
                       v[y]++;
                   }
                    x/=2;
                    y++;
                }
                i++;
                value=calculate(v,p,i-j);
            }
            ans=min(ans,abs(value-k));
            // cout<<i<<" "<<ans<<endl;
        }
        return ans;
    }
    int calculate(vector<int> &v,vector<int> &p,int s){
        int ans=0;
        for(int i=0;i<31;i++) if(v[i]==s) ans+=p[i];
        return ans;
    }
};
