class Solution {
    int k;
    vector<int> nums;
    vector<int> bit;
public:
    int minimumDifference(vector<int>& nums, int k) {
        this->k=k;
        this->nums=nums;
        bit.resize(32);
        int n=nums.size();
        set<int> ast, nst, rst;

        for (int i=0; i<n; i++){
            for (auto c: ast){
                nst.insert(c&nums[i]);
            }
            ast=nst;
            ast.insert(nums[i]);
            for (auto c: ast){
                rst.insert(abs(c-k));
            }
            // for (auto c: ast) cout << c << " "; cout << endl;
            nst.clear();
        }
        return *rst.begin();
    }
};
