class Solution {
public:

    void figuring(int n,vector<int> &bits)
    {
        for(int i=0;i<32;i++)
        {
            int set=((1<<i) & n) > 0 ? 1 : 0;
            bits[i]+=set;
        }
    }
    void removing(int n,vector<int> &bits)
    {
        for(int i=0;i<32;i++)
        {
            int set=((1<<i) & n) > 0 ? 1 : 0;
            bits[i]-=set;
        }
    }
    int minimumDifference(vector<int>& vec, int n) {
        int slide=0;
        int c=INT_MAX;
        int i=0;
        int j=0;
        vector<int> bits(32,0);
        while(i<vec.size())
        {
            figuring(vec[i],bits);
            slide=0;
            for(int g=0;g<32;g++)
            {
                int set=bits[g]==(i-j+1);
                slide+=pow(2,g)*set;
            }
            //cout<<slide<<"\n";
            c=min(c,abs(n-slide));
            while(j<=i && slide<n)
            {
                removing(vec[j],bits);
                j++;
                slide=0;
                for(int g=0;g<32;g++)
                {
                    int set=bits[g]==(i-j+1);
                    slide+=(long long)pow(2,g)*set;
                }
                //cout<<"--"<<slide<<"\n";
                c=min(c,abs(n-slide));
            }
            i++;
        }
        return c;
    }
};
