class Solution {
public:
    int calc(vector<int> &a , int f) 
    {
        long ans = 0 , p = 1;
        for(int i = 0 ; i < 32 ; i++) {
            if(a[i] == f) ans += p;
            p *= 2;
        }
        return ans;
    }
    void add(vector<int> &a , int p) {
        int i = 0;
        while(p) {
            if(p & 1) a[i]++;
            i++;
            p /= 2;
        }
    }
    void del(vector<int> &a , int p) {
        int i = 0;
        while(p) {
            if(p & 1) a[i]--;
            i++;
            p /= 2;
        }
    }
    int minimumDifference(vector<int>& nums, int k) {
        long n = nums.size() , ans = INT_MAX , p = 0;
        vector<int> a(32);
        long temp = INT_MAX;
        for(int i = 0 ; i < n ; i++) {
            if(p < n && (temp > k || p == i)) {
                do {
                    add(a , nums[p]);
                    int val = p - i + (p == n ? 0 : 1);
                    temp = calc(a , val);
                    // cout<<temp<<endl;
                    ans = min(ans , abs(temp - k));
                    p++;

                } while(p < n && temp > k);
            }
            // cout<<p<<endl;
            del(a , nums[i]);
            if(p == i + 1 || i == n - 1) continue ; 
            long val = p - i - 1;
            temp = calc(a , val);
            ans = min(ans , abs(temp - k));
        }
        return ans;
    }
};
