class Solution {
public:

    int getVal(int i , int j , vector<int>& nums, vector<vector<int>>& bits){

        if(i<0 || j>=nums.size())return 1e9;        

      if(i==j){
            return nums[i];
        }


        int val = 0;

        for(int p = 0 ; p<30 ; p++){

            int diff = j -i + 1;

            if(bits[i][p] - bits[j+1][p] == diff){
                val = val + pow(2,p);
            }



        }



        return val;



    }











    int helper(int idx , int k ,vector<int>& nums, vector<vector<int>>& bits ){

        int lo = idx;
        int hi = nums.size() - 1;

        int res = 1e9;
        while(lo<=hi){

            int mid = lo + (hi  - lo )/2;

             int val = getVal(idx,mid,nums,bits);

            if(val==k){
                return 0;
            }else if(val > k){
                res = min(res ,abs(k-val));
                lo = mid + 1;
            }else{
                 res = min(res ,abs(k-val));
                 hi = mid - 1;
            }   




        }






        return res;


    }








    int minimumDifference(vector<int>& nums, int k) {
        //nlogn
        int n = nums.size();
        vector<vector<int>> bits(n+1,vector<int> (30,0));

        for(int i = n-1 ; i >=0;  i--){
              int r = nums[i];
            for(int j = 0 ; j<30 ; j++){

                bits[i][j] = r%2 +  bits[i+1][j];

                r = r/2;
            }


        }

        int res = 1e9;
        for(int i = 0 ; i < n; i++){
            if(nums[i] == k){
              return 0;
            }
            res = min(res , abs(k - nums[i]));

            res = min(res , helper(i,k,nums,bits));



        }

        return res;



    }
};
