class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<int>an(32,0);
            int total=0;
            int finalans=1e9;
            int l=0;
            int r=-1;
            for(auto i:nums){
                    r++;
                    total++;
                    int ans=0;
                    for(int j=0;j<31;j++){
                            if((i>>j)&1) an[j]++;
                    }
                    for(int  i=0;i<31;i++){
                            if(an[i]==total ){
                                    ans+=1<<i;
                            }
                    }
                        finalans=min(abs(ans-k),finalans); 
                  //  cout<<finalans<<endl;
                    while(ans<=k) {
                            total--;
                            ans=0;

                            for(int j=0;j<31;j++){
                            if((nums[l]>>j)&1) an[j]--;
                    } 
                             for(int  i=0;i<31;i++){
                            if(an[i]==total && total!=0){
                                    ans+=1<<i;
                            }
                    }
                          if(total>0)  finalans=min(abs(ans-k),finalans);
                            l++;
                            if(l>r) break; 
                    }
            }
            return finalans;
    }
};
