class Solution {
public:

    int minimumDifference(vector<int>& nums, int k) {
        int ans = 1e9;
        int l=0,r=0;
        int ch= (1<<30) - 1;
        vector<int> freq(32,0);
        while(r<nums.size()){
            ch &= nums[r];
            for(int i=0;i<32;i++){
                int c = 1<<i;
                if(c<=nums[r] && nums[r]&c){
                    freq[i]++;
                }
            }
            if(ch<k){
                ans = min(ans,abs(ch-k));
                while(ch<k && r>l){
                    for(int i=0;i<32;i++){
                        int c = 1<<i;
                        if(c<=nums[l] && nums[l]&c){
                            freq[i]--;
                        }
                    }
                    int t = 0;
                    for(int i=0;i<32;i++){
                        if(freq[i]==(r-l)) {
                            t += (1<<i);
                        }
                    }
                    ans = min(ans,abs(t-k));
                    ch = t;
                    l++;
                }

            }
            r++;
            ans = min(ans,(abs(ch-k)));
        }
        return ans;
    }
};
