class Solution {
public:
    class Container {
        public:
        vector<int> rec;
        int cnt;
        Container(): rec(31, 0), cnt(0) {};

        int val() {
            int ans = 0;
            for(int i=0;i<31;i++) {
                if(rec[i] == cnt) {
                    ans += (1<<i);
                }
            }

            return ans;
        }

        void add(int val) {
            for(int i=0;i<31;i++) {
                if((val & (1<<i))) {
                    rec[i]++;
                }
            }
            cnt++;
        }

        void del(int val) {
            for(int i=0;i<31;i++) {
                if((val & (1<<i))) {
                    rec[i]--;
                }
            }
            cnt--;
        }
    };

    int minimumDifference(vector<int>& nums, int k) {
        Container rec;
        int n = nums.size();
        int ans = 1e9;
        int cur = 1e9;
        for(int l=0,r=0;r<n;r++) {
            rec.add(nums[r]);
            while(l < r && rec.val() < k) {
                ans = min(abs(rec.val()-k), ans);
                rec.del(nums[l]);
                l++;
            }
            //cout << l << " "<<r<<" "<<rec.val()<<endl;
            ans = min(abs(rec.val()-k), ans);
        }
        //cout << endl;

        return ans;
    }
};
