class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<vector<int>> index(30);
        for(int i=0;i<nums.size();++i){
            for(int j=0;j<30;++j){
                if((1<<j)&nums[i])continue;
                index[j].push_back(i);
            }
        }
        // for(int j=0;j<30;++j){
        //     cout<<index[j].size()<<endl;
        // }
        vector<int> nextInd(30);    for(int j=0;j<30;++j)nextInd[j]=(index[j].empty()?index[j].size():0);
        int res=INT_MAX;
        for(int i=0;i<nums.size();++i){
            map<int,vector<int>> stop;
            for(int j=0;j<30;++j){
                if(nextInd[j]!=index[j].size()&&index[j][nextInd[j]]<i)nextInd[j]++;
                if(nextInd[j]!=index[j].size()){
                    stop[index[j][nextInd[j]]].push_back(j);
                }
                // if(i==0&&nextInd[j]<index[j].size())cout<<j<<" "<<nextInd[j]<<" "<<index[j][nextInd[j]]<<endl;
            }
            int cur=(1<<30)-1;
            if(!stop.empty()&&stop.begin()->first>i){
                res=min(res,abs(cur-k));
            }
            for(auto &e:stop){
                for(auto &el:e.second){
                    cur^=(1<<el);
                }
                res=min(res,abs(cur-k));
                // cout<<nums[i]<<" "<<e.first<<" "<<cur<<" "<<i<<endl;
            }
        }
        return res;
    }
};
