/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var minimumDifference = function(nums, k) {

    let zeros = new Array(32).fill(0);


    const minus = (x) => {
        for (let i = 0; i < 32; ++i) {
            if (((x >> i) & 1) === 0) {
                zeros[i] -= 1;
            }
        }
    };


    const plus = (x) => {
        for (let i = 0; i < 32; ++i) {
            if (((x >> i) & 1) === 0) {
                zeros[i] += 1;
            }
        }
    };


    const eval = () => {
        let res = 0;
        for (let i = 0; i < 32; ++i) {
            if (zeros[i] === 0) {
                res ^= (1 << i);
            }
        }
        return res;
    };


    let i = 0;
    const n = nums.length;
    let ans = Number.MAX_SAFE_INTEGER;


    for (let j = 0; j < n; ++j) {
        plus(nums[j]);
        let x = eval();
        ans = Math.min(ans, Math.abs(x - k));
        while (i < j && x < k) {
            minus(nums[i]);
            ++i;
            x = eval();
            ans = Math.min(ans, Math.abs(x - k));
        }
    }

    return ans;
};

// Example usage:
console.log(minimumDifference([1, 2, 4, 5], 3));  
console.log(minimumDifference([1, 2, 1, 2], 2));  
console.log(minimumDifference([1], 10));
