class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<int>bits(32);
        int start=0;
        int n=nums.size();
        int curr=INT_MAX;
        int ans=INT_MAX;
        for(int i=0;i<n;i++){

            curr&=nums[i];
            for(int b=0;b<32;b++){
                bits[b]+=(nums[i]>>b)&1;
            }
            ans=min(ans,abs(k-curr));
            while(start<i && curr<k){
                for(int b=0;b<32;b++){
                    bits[b]-=(nums[start]>>b)&1;
                }
                for(int b=0;b<32;b++){
                    if(bits[b]==i-start) curr|=1<<b;
                }
                start++;
                ans=min(ans,abs(k-curr));
            }
            ans=min(ans,abs(k-curr));
        }
        return ans;
    }
};
