class Solution {
public:
    int getNum(vector<int>& arr)
    {
        bool empty = true;
        for (int i = 0; i < arr.size(); i++) {
            if (arr[i] != 0) {
                empty = false;
                break;
            }
        }
        if (empty) {
            return 0;
        }
        int res = 0;
        for (int i = 0; i < arr.size(); i++) {
            if (arr[i] == 0) {
                res += 1 * pow(2, i);
            }
        }
        return res;
    }

    void addNum(vector<int>& arr, int num)
    {
        for (int i = 0; i < arr.size(); i++) {
            if ((num & (1 << i)) == 0) {
                arr[i]++;
            }
        }
    }

    void removeNum(vector<int>& arr, int num)
    {
        for (int i = 0; i < arr.size(); i++) {
            if ((num & (1 << i)) == 0) {
                arr[i]--;
            }
        }
    }

    int minimumDifference(vector<int>& nums, int k)
    {
        vector<int> arr(32, 0);
        int left = 0;
        int right = -1;
        int res = INT_MAX;
        while (left < nums.size()) {
            int temp = left;
            // right = left - 1;
            // cout << left << " " <<right << endl;
            while ((left > right || getNum(arr) > k) && right + 1 < nums.size()) {
                addNum(arr, nums[right + 1]);
                ++right;
                // cout << left << " " << right << " " << getNum(arr) << endl;
                res = min(res, abs(getNum(arr) - k));
            }
            while (left <= right && getNum(arr) < k) {
                removeNum(arr, nums[left]);
                ++left;
                if (left > right) {
                    break;
                }
                // cout << left << " " << right << " " << getNum(arr) << endl;
                res = min(res, abs(getNum(arr) - k));
            }
            if (left == temp)
                break;
        }
        return res;
    }
};
