class Solution {
public:
    int fun(vector<int> nums,int k){
        int n=nums.size(),r=-1,val,ans=INT_MAX;
        vector<int> vis(31,0);
        for(int l=0;l<n;l++){
            if(l>r){
                for(int j=0;j<31;j++){
                    if(nums[l]&(1<<j)) vis[j]++;
                }
                val=nums[l];
                r=l;
            }

            while(r<n && val>k){
                r++;
                if(r<n){
                    int newVal=0;
                    for(int j=0;j<31;j++){
                        if((nums[r]&(1<<j))) vis[j]++;
                        if((nums[r]&(1<<j) )&& (val&(1<<j))) newVal|=(1<<j);
                    }
                    val=newVal;
                }
                ans=min(ans,abs(k-val));
            }

            ans=min(ans,abs(k-val));
            for(int j=0;j<31;j++){
                if(!(nums[l]&(1<<j)) && vis[j]==r-l){
                    val|=(1<<j);
                }
                if((nums[l]&(1<<j))) vis[j]--;
            }
            ans=min(ans,abs(k-val));
        }

        return ans;
    }
    int minimumDifference(vector<int>& v, int k) {
        int n=v.size(),ans=INT_MAX;
        for(int i=0;i<n;i++) ans=min(ans,abs(k-v[i]));
        vector<int> tem;
        for(int i=0;i<n;i++){
            if(v[i]>k){
                tem.push_back(v[i]);
            } else {
                if(tem.size()>0){
                    ans=min(ans,fun(tem,k));
                }
                tem.clear();
            }
        }
        if(tem.size()>0){
            ans=min(ans,fun(tem,k));
        }

        return ans;
    }
};
