class Solution {
public:
    pair<int, int> check(int mid, vector<int> &arr, int k){
        int n = arr.size();
        vector<int> bits(30);
        int ans = INT_MAX, diff = INT_MAX, mini = INT_MAX;
        for(int i = 0;i < mid;i++){
            for(int j = 0;j < 30;j++){
                if((arr[i] & (1 << j)))
                    bits[j]++;
            }
        }
        int x = 0;
        for(int i = 0;i < 30;i++){
            if(bits[i] == mid)
                x += (1 << i);
        }
        if(abs(x - k) < diff){
            diff = abs(x - k);
            ans = x;
            mini = x;
        }
        for(int i = mid;i < n;i++){
            for(int j = 0;j < 30;j++){
                if((arr[i] & (1 << j)))
                    bits[j]++;
            }
            for(int j = 0;j < 30;j++){
                if((arr[i-mid] & (1 << j)))
                    bits[j]--;
            }
            x = 0;
            for(int j = 0;j < 30;j++){
                if(bits[j] == mid)
                    x += (1 << j);
            }
            if(abs(x - k) < diff){
                diff = abs(x - k);
                ans = x;
            }
            mini = min(mini, x);
        }
        return {ans, mini};
    }
    int minimumDifference(vector<int>& arr, int k) {
        int n = arr.size();
        int l = 0,r = 0;
        int ans = INT_MAX;
        int x = 0;
        vector<int> bits(30);
        while(r < n){
            x = 0;
            for(int j = 0;j < 30;j++){
                if((arr[r] & (1 << j)))
                    bits[j]++;
            }
            for(int j = 0;j < 30;j++){
                if(bits[j] == (r - l + 1))
                    x += (1 << j);
            }

            ans = min(ans, abs(x - k));
            // cout<<l<<" "<<r<<" "<<x<<"t\n";
            if(x < k){
                while(x < k && l <= r){
                    for(int j = 0;j < 30;j++){
                        if((arr[l] & (1 << j)))
                            bits[j]--;
                    }
                    l++;
                    if(l > r)
                        break;
                    x = 0;
                    for(int j = 0;j < 30;j++){
                        if(bits[j] == (r - l + 1))
                            x += (1 << j);
                    }

                    // cout<<l<<" "<<r<<" "<<x<<"\n";


                    ans = min(ans, abs(x - k));

                }
            }

            if(l <= r)
                ans = min(ans, abs(x - k));
            r++;
        }
        return ans;
    }
};
