class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        map<int,vector<int>>m;
        int y=0;
        for(int i=0;i<nums.size();i++){
            if(i==0){
                y=nums[i];
            }
            else{
                y&=nums[i];
            }
            for(int j=0;j<32;j++){
                int e=1ll<<j;
                if(e&nums[i]){
                    m[j].push_back(i);
                }
            }
        }
        // int d=-1;
        int ans=1e9;
        for(int i=0;i<32;i++){
            int d=0;
            if(m[i].size()>0){
                d=m[i][0]-1;
                int v=nums[m[i][0]];
                for(int j=0;j<m[i].size();j++){
                    if(m[i][j]-d==1){
                        ans=min(ans,abs(k-(v&nums[m[i][j]])));
                        v&=nums[m[i][j]];
                        d=m[i][j];
                    }
                    else{
                        ans=min(ans,abs(k-nums[m[i][j]]));
                        v=nums[m[i][j]];
                        d=m[i][j];
                    }
                }
            }
        }
        ans=min(ans,abs(k-y));
        return ans;
    }
};
