class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int res = 2000000000;

        set<int> blog;
        vector<int> next_blog;
        for (int x : nums) {
            res = min(res, abs(x - k));
            if (res == 0)
                return 0;
            //if (x <= k)
            //    continue;
            next_blog.clear();
            next_blog.push_back(x);
            // printf("x = %d\n", x);
            for (int xx : blog) {
                // printf("xx before = %d, x = %d\n", xx, x);
                xx = xx & x;
                // printf("xx after = %d\n", xx);
                if (xx <= k && abs(xx - k) >= res)
                    continue;
                else {
                    res = min(res, abs(xx - k));  
                    // printf("x = %d, xx = %d, res = %d\n", x, xx, res);
                    next_blog.push_back(xx);
                }
            }
            blog.clear();
            for (int xx : next_blog)
                blog.insert(xx);
        }

        return res;
    }
};
