class Solution {
public:
    int chgCountSet(int num,int arr[],bool doInsert,int count){
        int ind=0;
        int dif=-1;
        if(doInsert) dif=1; 
        while(num){
            if(num&1){
                arr[ind]+=dif;
            }
            num>>=1;
            ind++;
        }
        // cout<<ind<<" ";
        int ans=0;
        if(count==0) return INT_MAX;
        for(int i=0;i<32;i++){
            if(arr[i]==count) ans|=(1<<i);
        }
        return ans;
    }
    int minimumDifference(vector<int>& nums, int k) {
        int ans=INT_MAX;
        int countSet[32]={};
        int i=0,j=0;
        int n=nums.size();
        int bitAnd=INT_MAX;
        while(j<n){
            while(j<n && bitAnd>=k){
                ans=min(ans,bitAnd-k);
                bitAnd=chgCountSet(nums[j],countSet,1,j-i+1);

                j++;
            }
            if(j==n) ans=min(ans,abs(bitAnd-k));
            while(bitAnd<k && i<j){
                ans=min(ans,k-bitAnd);
                bitAnd=chgCountSet(nums[i],countSet,0,j-i-1);

                i++;
            }
            // cout<<bitAnd<<" ";

        }
        ans=min(ans,abs(bitAnd-k));
        // cout<<chgCountSet(15,countSet,1,1)<<endl;
        // cout<<chgCountSet(7,countSet,1,2)<<endl;
        // cout<<chgCountSet(3,countSet,1,3)<<endl;
        // cout<<chgCountSet(1,countSet,1,4)<<endl;
        // cout<<chgCountSet(1,countSet,0,3)<<endl;
        // cout<<chgCountSet(3,countSet,0,2)<<endl;
        // cout<<chgCountSet(15,countSet,0,1)<<endl;
        // cout<<chgCountSet(7,countSet,0,0)<<endl;
        return ans;
    }
};
