class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
       // unordered_map<int,long long>um;
        vector<long long>v;
        int n=nums.size();
        int s=0;int ans=INT_MAX;
        for(int i=0;i<n;i++){
            v.push_back(nums[i]);
            int diff=abs(nums[i]-k);
            ans=min(ans,diff);
            int mini=INT_MAX;
            for(int j=s;j<i;j++){
                v[j]=v[j]&nums[i];
                 int dif=abs(v[j]-k);
            ans=min(ans,dif);
                if(v[j]<=mini){
                    mini=v[j];
                    s=j;
                }
            }

            }
        return ans;

    }
};
