class Solution {
    public int minimumDifference(int[] nums, int k) {
        TreeSet<Integer> set = new TreeSet<>();
        int n = nums.length;
        int res = Integer.MAX_VALUE;
        for (int i = 0; i < n; i++) {
            int val = nums[i];
            TreeSet<Integer> set2 = new TreeSet<>();
            set2.add(val);
            for (int kk : set) {
                set2.add(val & kk);
            }
            set = set2;
            for (int kk : set2) {
                res = Math.min(res, Math.abs(k - kk));
            }
        }
        return res;
    }
}
