class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<int> in(32,-1);

        int ans=INT_MAX,ad=nums[0];

        for(int i=0;i<nums.size();i++){
            ad=ad&nums[i];
            ans=min(ans,abs(ad-k));
            int h=nums[i],j=0;

            while(j<32){
                if(h&1){
                    if(in[j]==-1){
                        in[j]=i;
                    }
                }else{
                    in[j]=-1;
                }
                h=h>>1;
                ++j;
            }

            int f=0;

            vector<vector<int>> t;
            for(int j=0;j<32;j++){
                if(in[j]!=-1)
                t.push_back({in[j],j});
            }

            sort(t.begin(),t.end());

            j=0;

            while(j<t.size()){
                h=j;
                while(h<t.size()&&t[h][0]==t[j][0]){

                    f=f|(1<<t[h][1]);
                    ++h;
                }
                ans=min(ans,abs(f-k));
                j=h;
            }


        }

        return ans;
    }
};
