class Solution {
public:
    int func(int i,int mid,vector<vector<int>>&pf){
                int sum=0;
                for(int j=0;j<31;j++){
                    int cnt=pf[i+1][j]-pf[mid][j];
                    if(cnt>0&&(cnt==(i-mid+1)))sum+=(1<<j);
                }
        return sum;
    }
    int minimumDifference(vector<int>& nums, int k) {

        int n=nums.size();

        vector<vector<int>>pf(n+1,vector<int>(31,0));
        for(int i=0;i<n;i++){

            int x=nums[i];
            for(int j=0;j<31;j++){
                if(x&(1<<j))pf[i+1][j]++;
                pf[i+1][j]+=pf[i][j];
            }
        }


        int ans=1e9;
        for(int i=0;i<n;i++){
            if(nums[i]<k)continue;
            int lo=0,hi=i;
            while(hi-lo>1){
                int mid=(lo+hi)/2;

                int sum=func(i,mid,pf);

                if(sum<k)lo=mid;
                else hi=mid;
            }

            int sum1=func(i,lo,pf);
            int sum2=func(i,hi,pf);

            //cout<<lo<<" "<<sum1<<" "<<hi<<" "<<sum2<<" "<<ans<<endl;
            ans=min(ans,abs(k-sum1));
            ans=min(ans,abs(k-sum2));
        }

        //cout<<ans<<endl;
        for(int i=0;i<n;i++){
            ans=min(ans,abs(nums[i]-k));
        }


        return ans;
    }
};
