class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        int i,j,n,now;
        n=a.size();
        bitset<100000> cap;
        bitset<100000> v;
        cap=0;now=0;
        sort(a.begin(),a.end());
        v=0;
        v.set(0);
        for (i=0;i<a.size();i++)
        {
            while (now<a[i])
            {
                cap.set(now);now++;
            }
            v|=((v&cap)<<a[i]);
        }
        for (i=99999;i>=0;i--)
            if (v.test(i)) return i;
        return -1;
    }
};
