class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        bitset<(int)1e5+1> dp;
        dp[0]=1;
        bitset<(int)1e5+1> u;
        sort(rewardValues.begin(), rewardValues.end());
        int j = 0;
        for(int& i: rewardValues){
            while(j<i){
                u[j]=1;
                j++;
            }
            dp|=(dp&u)<<i;
        }
        j = rewardValues.back()*2-1;
        while(!dp[j])j--;
        return(j);
    }
};
