class Solution {
public:

    int maxTotalReward(vector<int> &rewardValues) {
        sort(rewardValues.begin(), rewardValues.end());

        bitset<(int)1e5 + 5> dp;
        bitset<(int)1e5 + 5> dp2;
        dp[0] = 1;


        dp.reset(1);

        for (int i = 0; i < rewardValues.size(); ++i) {
            for (int j = (i?rewardValues[i - 1]:0); j < rewardValues[i]; ++j) {
                dp2[j] = 1;
            }
            dp |= (dp&dp2)<<rewardValues[i];
        }

        for (int i = 1e5 + 4; i >= 0; --i) {
            if(dp[i])
                return i;
        }
        return 0;
    }

};
