class Solution {
public:

    bool dp[50006];
    int maxTotalReward(vector<int>& rewardValues) {
        int n = rewardValues.size();
        sort(begin(rewardValues), end(rewardValues));
        int dm = unique(begin(rewardValues), end(rewardValues)) - begin(rewardValues);
        int L = rewardValues[dm-1];

        dp[0] = 1;
        for(int i = 0; i < dm; i ++) {
            int up = rewardValues[i];
            if(L-rewardValues[i] < up) up = L-rewardValues[i];
            for(int j = 0; j < up; j ++)
                if(dp[j]) dp[j+rewardValues[i]] = 1;
        }

        int ans = L;
        for(int i = L-1; i >= 0; i --)
            if(dp[i]) return L + i;
        return ans;
    }
};
