class Solution {
 public:
  int maxTotalReward(vector<int>& rewardValues) {
    sort(rewardValues.begin(), rewardValues.end());
    rewardValues.erase(unique(rewardValues.begin(), rewardValues.end()),
                       rewardValues.end());
    int n = rewardValues.size();
    bitset<50001> dp, mask;
    dp[0] = 1;
    for (int i = 0; i < n - 1; i++) {
      // clear all bits greater than rewardValues[i]
      for (int j = (i ? rewardValues[i - 1] : 0); j < rewardValues[i]; j++) {
        mask[j] = 1;
      }
      dp |= (dp & mask) << rewardValues[i];
    }
    for (int i = rewardValues.back() - 1; i >= 0; i--) {
      if (dp[i]) return i + rewardValues.back();
    }
    return 0;
  }
};
