const int N = 1e5 + 1 ; 

class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) 
    {
        sort((rewardValues.begin()) , rewardValues.end()) ; 
        vector < int > vc ; 
        for(auto a : rewardValues)
         if(vc.size() == 0 or vc.back() != a)
            vc.push_back(a) ; 
        int n = vc.size() ; 
        int ans = 0 ; 
        bitset < N + 1 >bt ; 
        bitset < N + 1 >bits ; 
        int cur = -1 ; 
        bt[0] = 1 ; 
        for(auto a : vc)
        {
            while(cur < a)
            {
               if(cur + 1 == a)break ; 
               cur ++  ; 
               bits[cur] = 1 ; 
            }
            auto here = bt ;  
            here &= bits; 
            bt |= (here << a) ; 
        }
        for(int i = 0 ; i <= N ; i += 1)
         if(bt[i])
            ans = max(ans , i) ; 
         return ans ; 
    }
};
