class Solution:
    def maxTotalReward(self, rs: List[int]) -> int:
        sr = set(rs)
        rs = list(sr)
        rs.sort()
        n = len(rs)

        minv = min(rs)

        @cache
        def dp(val):
            if val in sr:
                return True
            if val < minv:
                return False
            ind = bisect_left(rs, (val + 2) // 2)
            for i in range(ind, n):
                if rs[i] > val:
                    return False
                if dp(val - rs[i]):
                    return True
            return False

        mv = max(rs)
        for i in range(2*mv-1, mv, -1):
            if dp(i):
                return i
        return mv
