bitset <100000> bits, buffer;

class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        sort(rewardValues.begin(), rewardValues.end());
        rewardValues.resize(unique(rewardValues.begin(), rewardValues.end()) - rewardValues.begin());
        bits.reset();
        bits[0] = 1;
        for (int i : rewardValues) {
            buffer = (bits << (100000 - i)) >> (100000 - i);
            buffer <<= i;
            bits |= buffer;
        }
        int maximum = 0;
        for (int i = 1; i < (rewardValues.back() << 1); ++i) {
            if (bits[i]) {
                maximum = max(maximum, i);
            }
        }
        return maximum;
    }
};
