class Solution {
public:
    int maxTotalReward(vector<int>& a) {
         sort(a.begin(), a.end());
        a.erase(unique(a.begin(), a.end()), a.end());
        int n = (int) a.size();
        int mx = a.back();

        vector<bool> dp(50001);

        if (n > 1 && a[n - 1] == a[n - 2]+1) return a[n-1] + a[n-2];

        for (int i : a) dp[i] = true;

        for (int i = 0; i < n; i++) {
            // vector<bool> done(mx);
            for (int j = 0; j < mx; j++) {
                if (a[i] + j >= mx) break;
                if (j < a[i] && dp[j]) {
                    dp[a[i] + j] = true;
                    // done[a[i] + j] = true;
                }
            }
        }

        int ans = 0;
        for (int i = 0; i < mx; i++) if (dp[i]) ans = i;

        return ans + mx;
    }
};
