using namespace std;
using ll = long long;


class Solution {
public:
    int maxTotalReward(vector<int>& A) {
        sort(A.begin(), A.end());
        A.erase(unique(A.begin(), A.end()), A.end());
        int n = (int)A.size();
        const int N = 200001;
        bitset<N> dp;
        dp[0] = 1;
        bitset<N> full;
        int r = 0;
        for (int i = 0; i < n; i++) {
            while (r < A[i]) {
                full[r] = 1;
                r += 1;
            }
            auto t = dp & full;
            t <<= A[i];
            dp |= t;
        }

        for (int i = N - 1; i >= 0; i--) {
            if (dp[i]) return i;
        }
        return 0;
    }
};
