class Solution {
public:


    int maxTotalReward(vector<int>& a) {
        sort(a.begin(), a.end());

        const int N=100010;
        bitset<N> all;
        for(int i=0;i<N;i++) all.set(i);

        bitset<N> v;
        v[0]=1;
        for(const int& x: a) {
            bitset<N> mask = (all>>(N-x));
            bitset<N> who=(v & mask)<<x;
            v|=who;
            // cout<<x<<"::"<<who<<"::"<<mask<<"::"<<v<<endl;
        }
        for(int i=N-1;i>=0;i--) if(v[i]) return i;
        return 0;
    }
};
