class Solution {
    public int maxTotalReward(int[] rewardValues) {

        int res = 0;
        Arrays.sort(rewardValues);
        int n = rewardValues.length;
        res = rewardValues[n - 1];
        res += helper(rewardValues, n - 2, rewardValues[n - 1]);
        return res;
    }

    public int helper(int[] rewardValues, int endIndex, int target) {
        // System.out.println("endIndex = " + endIndex + "    sum target = " + target);
        if (endIndex < 0) {
            return 0;
        }
        int res = 0;
        for (int i = endIndex; i >= 0; i--) {
            if (i < endIndex - 1 && rewardValues[i] == rewardValues[i + 1]) continue;
            int tmp = 0;
            if (rewardValues[i] < target) {
                tmp = rewardValues[i] + helper(rewardValues, i - 1, Math.min(rewardValues[i], target - rewardValues[i])); 
                if (tmp == target - 1) return tmp;
                res = Math.max(res, tmp);
                // System.out.println("endIndex =" + endIndex + "  tmp = " + tmp + "       res = " + res);
            } 

        }
        return res;
    }
}
