class Solution:
    def maxTotalReward(self, rewardValues: List[int]) -> int:
        rewardValues.sort()
        @lru_cache
        def helper(target):
            i = bisect_left(rewardValues, target)
            if i < len(rewardValues) and rewardValues[i] == target:
                return rewardValues[i]
            best = 0
            while i > 0:
                i -= 1
                best = max(best, rewardValues[i] + helper(min(target - rewardValues[i], rewardValues[i] - 1)))
            return best
        res = rewardValues.pop()
        return res + helper(res - 1)
