class Solution {
    int[] ans = new int[50001];
    public int maxTotalReward(int[] a) {
        Arrays.sort(a);
        for (int i=a[a.length-1]-1; i>=0; i--)
            if (solve(a, a.length-2, i))
                return i + a[a.length-1];
        return a[a.length-1];
    }
    public boolean solve(int[] a, int d, int z) {
        if (z < 0)
            return false;
        if (d < 0)
            return z == 0;
        if (ans[z] != 0)
            return ans[z] == 1;
        if (z >= a[d] * 2) {
            ans[z] = -1;
            return false;
        }
        if (z == 0) {
            ans[z] = 1;
            return true;
        }
        ans[z] = solve(a, d-1, z - a[d]) || solve(a, d-1, z) ? 1 : -1;
        return ans[z] == 1;
    }
}
