class Solution {
public:
    int maxTotalReward(vector<int>& r) {
        sort(r.begin(), r.end());
        vector<int> r2={r[0]};

        for (int i=1; i<r.size(); ++i) {
            if (r[i]==r[i-1]) continue;
            r2.emplace_back(r[i]);
        }

        swap(r, r2);

        int n=r.size(), res=0;
        vector<bool> temp(r.back()*2+1);
        temp[0]=true;

        for (int i=1; i<=temp.size(); ++i) {
            int l=0, t=n-1;
            int j=(i>>1);
            while(l<=t) {
                int mid=(l+t)>>1;
                if (r[mid]<=j) l=mid+1;
                else t=mid-1;
            }
            if (l==r.size()) break;

            for (int k=l; k<n; ++k) {
                if (r[k]>i) break;
                if (temp[i-r[k]]==true) {
                    temp[i]=true;
                    res=i;
                    break;
                }
            }

        }

        return res;
    }
};
