#define mxn 100005//

ll n;
bitset<mxn>dp, tmp;

class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        n=rewardValues.size();
        sort(all(rewardValues));
        dp.reset();
        tmp.reset();
        dp[0]=1;
        fr(n){
            tmp=(dp<<(mxn-(rewardValues[i]-1)-1))>>(mxn-(rewardValues[i]-1)-1);
            tmp|=(tmp<<rewardValues[i]);
            dp|=tmp;
        }
        ll ans=0;
        for(int i=0; i<=2*rewardValues[n-1]; i++){
            if(dp[i]) ans=i;
        }
        return ans;
    }
};
