#define ll long long

class Solution {
public:

    int maxTotalReward(vector<int>& a) {
        sort(a.begin(), a.end());

        ll maxx = a.back();

        const ll nn = 2 * maxx + 1;

        bitset<100002> dp;
        bitset<100002> allOne;

        ll c = 0;

        dp[0] = 1;

        for(auto i:a){
            while(c < i){
                allOne[c] = 1;
                c++;
            }
            dp |= (dp & allOne) << i;
        }

        for(ll i = nn - 1; i>=0; i--){
            if(dp[i]){
                return i;
            }
        }

        return 0;
    }
};
