class Solution {
public:

    bitset<50005> ans;
    int maxTotalReward(vector<int>& rv) {
        ans = 0;
        ans[0] = 1;

        sort(rv.begin() , rv.end());
        int n = rv.size();

        for(int i = 0 ; i <  n-1 ; i ++)
        {
            int x = rv[i];
            ans = ((ans ^ ((ans >> x) << x)) << x) | ans;
        }


        for(int  p = rv[n-1]-1; p >= 0 ; p --)
        {
            if(ans[p])
            {
                return p + rv[n-1];
            }
        }
        return 0;
    }
};
