from sortedcontainers import SortedList

class Solution:
    def maxTotalReward(self, rewardValues: List[int]) -> int:
        ss = set(rewardValues)
        rewardValues = SortedList(ss)
        @cache
        def Max(pre_i, target):
            if target - 1 in ss:
                return target - 1
            if pre_i < 0:
                return 0
            if rewardValues[pre_i] < target:
                return max(Max(pre_i-1, target), rewardValues[pre_i] + Max(pre_i-1,min(rewardValues[pre_i], target-rewardValues[pre_i])))
            else:
                return Max(rewardValues.bisect_left(target) - 1, target)
        if len(rewardValues) == 1:
            return rewardValues[-1]
        return Max(len(rewardValues) - 2, rewardValues[-1]) + rewardValues[-1]
