class Solution {
    public int maxTotalReward(int[] rewardValues) {
        Arrays.sort(rewardValues);
        int m = rewardValues[rewardValues.length - 1];
        boolean[] visited = new boolean[m + 1];
        if(m==rewardValues.length){
            return 2*m-1;
        }
        visited[0] = true;
        int ans = m;
        int pre = -1;
        for (int num : rewardValues) {
            if(num==pre)  continue;
            pre = num;
            for (int i = num - 1; i >= 0; i--) {
                if (visited[i]) {
                    if (i + num > m) {
                        ans = Math.max(ans, i + num);
                    } else {
                        visited[i + num] = true;
                    }
                }
            }
        }

        return ans;




    }
}
