class Solution:
    def maxTotalReward(self, rewardValues: List[int]) -> int:
        arr = [0] + sorted(set(rewardValues))
        @cache
        def helper(num):
            i = bisect_left(arr, num)
            if arr[i] == num or i == 0:
                return arr[i]
            res = arr[i-1]
            for ind in range(i-1, -1, -1):
                res = max(res, arr[ind] + helper(min(num - arr[ind], arr[ind]-1)))
                if arr[ind] * 2 <= num:
                    break
            return res
        mx = arr[-1]
        return helper(mx-1) + mx
