class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        int n = a.size();
        sort(begin(a), end(a));

        bitset<100000> dp, mask;
        dp[0] = 1;

        int curr = 0;
        for(int i = 0; i < n; i++){
            while(curr < a[i]) mask[curr++] = 1;
            dp |= ((dp & mask) << a[i]);

        }
        for(int i = 100000 - 1; i >= 0; i--) {
            if(dp[i]) return i;
        }
        return 0;
    }
};
